package homework;

public class _01_Basic01 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// 문제 1) int num = 10;을 선언하고 증감연산자를 통해 다음과 같이 출력하세요. 출력된 값은 모두 증감연산자가 포함된 상태입니다.
		int num = 10;
		
		// (1) 출력: 11  | num: 11
		System.out.println(++num);
		
		// (2) 출력: 12  | num: 12
		System.out.println(++num);
		
		// (3) 출력: 12  | num: 11
		System.out.println(num--);
		
		// (4) 출력: 10  | num: 10
		System.out.println(--num);
		
		// (5) 출력: 10  | num: 9
		System.out.println(num--);
		
		// (6) 출력: 8  | num: 8
		System.out.println(--num);
		
		
		
		
		
		
		
		
		
	}

}
