package homework;

public class _02_Basic02 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// 문제 2) 관계연산자(<, >) 두 개를 사용하여 int a = 10; int b = 20; 의 관계를 다음과 같이 출력하세요.
		int a = 10;
		int b = 20;
		
		//(1) true 출력
		System.out.println("a < b : " + (a < b));
		
		
		// (2) false 출력
		System.out.println("a > b : " + (a > b));
	}

}
